import { ITarefaEnum } from './../../shared/models/ITarefa';
import { Component, OnInit } from '@angular/core';
import { TarefaService } from 'src/app/shared/service/tarefa.service';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-editar',
  templateUrl: './editar.component.html',
  styleUrls: ['./editar.component.scss']
})
export class EditarComponent implements OnInit {

  public atividades: ITarefaEnum[] = [];
  public tarefaEmEdicao: ITarefaEnum | null = null;

  constructor(private tarefaService: TarefaService, private http: HttpClient) { }

  ngOnInit(): void {
    this.getAtividades();
  }

  private getAtividades(): void {
    this.tarefaService.getAtividades().subscribe(atividades => this.atividades = atividades);
  }

  public editarTarefa(tarefa: ITarefaEnum): void {
    if (this.tarefaEmEdicao && this.tarefaEmEdicao.id === tarefa.id) {
      this.tarefaEmEdicao.editando = !this.tarefaEmEdicao.editando; // Alternar o valor da propriedade 'editando'
    } else {
      if (this.tarefaEmEdicao) {
        this.tarefaEmEdicao.editando = false; // Fechar a caixa de edição da tarefa anterior
      }
      this.tarefaEmEdicao = { ...tarefa, editando: true }; // Abrir a caixa de edição da tarefa atual
    }
  }

  public salvarTarefa(): void {
    if (this.tarefaEmEdicao) {
      this.tarefaService.atualizarAtividade(this.tarefaEmEdicao).subscribe(() => {
        this.getAtividades(); // Atualiza as atividades no navegador
        this.tarefaEmEdicao = null; // Limpa a tarefa em edição após salvar
      });
    }
  }

}
